  //      return(
  //        null
  //      )
  //   } else {
  //       return(
  //         <ul>
  //           {persons.map(name => 
  //             <li key={name.id} >{name.content}</li> 
  //           )} 
  //         </ul> 
  //       )
  //     }
  // }

  return (
    <div>
      <h2>Phonebook</h2>

      <form onSubmit={addPerson}>
        name:  
        <input value={newName} 
               onChange={handleNameChange}/>
        <button type="submit">add</button>
      </form>
      
      <h2>Numbers</h2>

      <div>
      <ul>
           {persons.map(name => 
             <li key={name.id} >{name.content}</li> 
           )} 
         </ul> 
      </div>
      
    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
